    fp1=fopen("test.in","r");
    fp2=fopen("testA1.out","w");

    /*Main function*/
    int n,c,k;//flag=0 means we haven't found the pair,flag=1 means we have found the pair.
    fscanf(fp1,"%d",&amp;n);
    for(int i=1;i&lt;=n;i++)
        fscanf(fp1,"%d",&amp;num[i]);
